mapper.select:
select *
from notice
where noticeno = #{ value }

select * from notice     
 where noticedate between #{begin} and #{end} 
 order by noticeno desc

select *
from qna
where qnano = #{ value }

mapper.insert:
insert into qna values 
((select max(qnano) from qna) + 1, 
#{userid},null, #{qnatype}, #{qnatitle},#{qnacontent},#{questiondate} ,#{qnaanswer} ,#{answerdate} 
, #{original_filepath}, #{rename_filepath}

mapper.update:
update qna set qnatitle = #{qnatitle}, noticecontent = #{noticecontent} 
, original_filepath = #{original_filepath}, rename_filepath = #{rename_filepath} 
where qnano = #{qnano}
